function openModal(event, id_agence) {
    event.preventDefault();
  
    $('#supprimer').modal('show');
    $("#hiddenValue").val(id_agence);
}


function closeModal() {
    var modal = document.getElementById('supprimer');
    $(modal).modal('hide');
}




$(document).ready(function(){
    const urlParams = new URLSearchParams(window.location.search);
    const deleteAgence = urlParams.get("delete");
    if (deleteAgence === 'true') {
        $("#supprimerResponse").modal('show');
    }
});

function closeModals() {
    var modal = document.getElementById('supprimerResponse');
    $(modal).modal('hide');
}

$(document).ready(function() {
    $('body').on('click', function() {
        if (window.location.search.includes('delete=true')) {
            history.replaceState({}, document.title, window.location.pathname);
        }
    });
});

